from html.parser import HTMLParser
class TextExtractor(HTMLParser):
    def __init__(self):
        super().__init__()
        self.in_script=False
        self.in_style=False
        self.text=[]
    def handle_starttag(self, tag, attrs):
        if tag.lower()=='script':
            self.in_script=True
        if tag.lower()=='style':
            self.in_style=True
    def handle_endtag(self, tag):
        if tag.lower()=='script':
            self.in_script=False
        if tag.lower()=='style':
            self.in_style=False
    def handle_data(self, data):
        if not (self.in_script or self.in_style):
            text=data.strip()
            if text:
                self.text.append(text)
import pathlib
path=pathlib.Path('logs/search/fetch-20260213-173007/4.txt')
data=path.read_text(encoding='utf-8', errors='ignore')
parser=TextExtractor()
parser.feed(data)
print('\\n'.join(parser.text[:60]))
PY"

