from html.parser import HTMLParser
from pathlib import Path
import sys

class Extractor(HTMLParser):
    def __init__(self):
        super().__init__()
        self.result = []
        self.skip = False
        self.depth = 0

    def handle_starttag(self, tag, attrs):
        if tag in ('script','style','noscript'):
            self.skip = True
        if tag in ('p','li','h1','h2','h3','h4','h5','h6','strong','b'):
            self.result.append('\\n')
        if tag == 'br':
            self.result.append('\\n')

    def handle_endtag(self, tag):
        if tag in ('script','style','noscript'):
            self.skip = False
        if tag in ('p','li','h1','h2','h3','h4','h5','h6'):
            self.result.append('\\n')

    def handle_data(self, data):
        if not self.skip:
            self.result.append(data)

    def text(self):
        return ''.join(self.result)

for file in sorted(Path('logs/search/fetch-20260213-173354').glob('*.txt')):
    print('---', file.name)
    html = file.read_text(errors='ignore')
    parser = Extractor()
    parser.feed(html)
    text = parser.text()
    text = '\\n'.join(line.strip() for line in text.split('\\n') if line.strip())
    print(text[:2000])
    print('...\\n')
PY"

